/*jshint globalstrict: true, esversion: 6 */

'use strict';

import {AbstractFunctions} from "AbstractFunctions";

/**
 * Functions is the default implementation of {@link FunctionsInterface} which
 * provides routine Functions methods that are commonly used in the framework.
 *
 * {@link AbstractFunctions} is basically a base class for various methods/routines
 * which this class extends. Please look to the Abstract Class for more information.
 *
 * Method list: (+) @api, (-) protected or private visibility.
 *
 * (+) {Object} constructor();
 * (+) {void}   setConstants();
 * (+) {void}   setProperties();
 *
 * @author Daryl Eisner <deisner@ucsd.edu>
 */
class Functions extends AbstractFunctions
{
    /**
     * Constants.
     *
     * @var {string}  version The version number
     *
     * @api
     */
    setConstants()
    {
        this.version = '1.28.0';
    }

    //--------------------------------------------------------------------------

    /**
     * Properties.
     */
    setProperties()
    {
    }

    //--------------------------------------------------------------------------

    /**
     * Constructor.
     *
     * @api
     */
    constructor()
    {
        /* Initialize Parent */
        super();
        super.setConstants();
        super.setProperties();

        /* Initialize Child */
        this.setConstants();
        console.log('Class instance was created for: ' + this.constructor.name);
    }

    //--------------------------------------------------------------------------
}

export default Functions;
